import { createContext, ReactNode, useContext, useState } from "react";
import { ProductPros } from "../../types";

interface CartProps {
  children: ReactNode;
}

interface CartProviderDataProps {
  cart: ProductPros[];
  addProduct: (product: ProductPros) => void;
  deleteProduct: (product: ProductPros) => void;
}

const CartContext = createContext<CartProviderDataProps>(
  {} as CartProviderDataProps
);

export const CartProvider = ({ children }: CartProps) => {
  const [cart, setCart] = useState<ProductPros[]>([]);

  const addProduct = (product: ProductPros) => {
    if (!cart.includes(product)) {
      setCart([...cart, product]);
    }
  };

  const deleteProduct = (productToBeDeleted: ProductPros) => {
    const newCart = cart.filter(
      (product) => product.id !== productToBeDeleted.id
    );
    setCart(newCart);
  };

  return (
    <CartContext.Provider value={{ cart, addProduct, deleteProduct }}>
      {children}
    </CartContext.Provider>
  );
};

export const useCart = () => {
  return useContext(CartContext);
};
