import { useCart } from "../../Providers/Cart";

function ProductCard({ product, isCart }) {
  const { addProduct, deleteProduct } = useCart();

  return (
    <div>
      <img src={product.image} alt={product.title} />
      <h4>{product.title}</h4>
      <h5>{`R$ ${product.price.toFixed(2)}`}</h5>
      {isCart ? (
        <button onClick={() => deleteProduct(product)}>Remover</button>
      ) : (
        <button onClick={() => addProduct(product)}>Adicionar</button>
      )}
    </div>
  );
}

export default ProductCard;
