import { createContext, ReactNode, useContext, useState } from "react";

interface ICartProps {
  children: ReactNode;
}

interface IProduct {
  id: number;
  title: string;
  description: string;
  price: number;
  image: string;
}

interface ICartProviderData {
  cart: IProduct[];
  addProduct: (product: IProduct) => void;
  removeProduct: (product: IProduct) => void;
}

const CartContext = createContext<ICartProviderData>({} as ICartProviderData);

export function CartProvider({ children }: ICartProps) {
  const [cart, setCart] = useState<IProduct[]>([]);

  function addProduct(product: IProduct) {
    if (!cart.includes(product)) {
      setCart([...cart, product]);
    }
  }

  function removeProduct(productToBeRemoved: IProduct) {
    const updatedCart = cart.filter(
      (product) => product.id !== productToBeRemoved.id
    );
    setCart(updatedCart);
  }

  return (
    <CartContext.Provider value={{ cart, addProduct, removeProduct }}>
      {children}
    </CartContext.Provider>
  );
}

export function useCart() {
  return useContext(CartContext);
}
